package com.core;

public class FirstCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello World....!");
		
		// Variables
		
		int a = 23; // 4 bytes
		double percent =63.50;//8 bytes
		float percent1 = 63.50f;// 4bytes
		long l =50000000l;//8 bytes
		char c='A';
		
		
		
		
		System.out.println("Value of a : "+a);
		System.out.println("Double value : "+percent);
		System.out.println("Float Value : "+percent1);
		
		double d1=5;// Implicit Type Casting
		int k =(int)5.7;// Explicit type casting
		
		/*
		 * byte -->short --> int --> long --> float --> double
		 * 
		 * 
		 * */
		System.out.println("Value of K  : "+k);
	}

}
