package com.core;

public class Operators {

	
	
		/*
		 * Arithmetic
		 * 		-> + , -, *, / , %		
		 * 
		 * 
		 * Bitwise
		 * 
		 * Relational
		 * 
		 * Logical
		 * 
		 * 
		 * */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int m=78, n=34;
			System.out.println("Addition : "+(m+n)+"\n Substraction : "+(m-n)+"\n Multiplication "+(m*n)+"\n Division "+(m/n)+"\n Mudulus Division "+(m%n));
			
			// Short hand Opertaor
			// n=n+m; instead
			n+=m;
			System.out.println(n);
	}

}
